import React, { useEffect, useState } from 'react';
import axios from 'axios';
import '../styles/StudentsPage.css';
import StudentModal from '../components/StudentModal';

function StudentsPage() {
  const [students, setStudents] = useState([]);
  const [search, setSearch] = useState('');
  const [filtreDept, setFiltreDept] = useState('');
  const [filtreFiliere, setFiltreFiliere] = useState('');
  const [filtreGroupe, setFiltreGroupe] = useState('');

  // Modale détails/édition
  const [modalOpen, setModalOpen] = useState(false);
  const [modalMode, setModalMode] = useState('details'); // 'details' ou 'edit'
  const [selectedStudent, setSelectedStudent] = useState(null);

  // Modale suppression
  const [deleteModalOpen, setDeleteModalOpen] = useState(false);
  const [studentToDelete, setStudentToDelete] = useState(null);

  useEffect(() => {
    // Appel API pour récupérer la liste des étudiants (mock pour l'instant)
    axios.get('http://localhost:8000/api/etudiants/')
      .then(res => setStudents(res.data))
      .catch(() => setStudents([]));
  }, []);

  const handleShowDetails = (student) => {
    setSelectedStudent(student);
    setModalMode('details');
    setModalOpen(true);
  };

  const handleEdit = (student) => {
    setSelectedStudent(student);
    setModalMode('edit');
    setModalOpen(true);
  };

  const handleSave = (updatedStudent) => {
    axios.put(`http://localhost:8000/api/etudiants/${updatedStudent.id}/`, updatedStudent)
      .then(res => {
        setStudents(students.map(s => s.id === updatedStudent.id ? res.data : s));
        setModalOpen(false);
      });
  };

  const handleDelete = (student) => {
    setStudentToDelete(student);
    setDeleteModalOpen(true);
  };

  const confirmDelete = () => {
    axios.delete(`http://localhost:8000/api/etudiants/${studentToDelete.id}/`)
      .then(() => {
        setStudents(students.filter(s => s.id !== studentToDelete.id));
        setDeleteModalOpen(false);
      });
  };

  // Filtres et recherche (simple)
  const filtered = students.filter(s =>
    (!search || s.nom.toLowerCase().includes(search.toLowerCase()) || s.prenom.toLowerCase().includes(search.toLowerCase()) || s.code_apogee.includes(search)) &&
    (!filtreDept || s.departement === filtreDept) &&
    (!filtreFiliere || s.filiere === filtreFiliere) &&
    (!filtreGroupe || s.groupe === filtreGroupe)
  );

  return (
    <div className="students-page">
      <div className="students-header">
        <h2>Étudiants</h2>
        <button className="btn-ajouter">+ Ajouter un étudiant</button>
      </div>
      <div className="students-filtres">
        <input
          type="text"
          placeholder="Rechercher par nom, prénom ou apogée..."
          value={search}
          onChange={e => setSearch(e.target.value)}
        />
        <select value={filtreDept} onChange={e => setFiltreDept(e.target.value)}>
          <option value="">Département</option>
          {/* Options dynamiques à ajouter */}
        </select>
        <select value={filtreFiliere} onChange={e => setFiltreFiliere(e.target.value)}>
          <option value="">Filière</option>
          {/* Options dynamiques à ajouter */}
        </select>
        <select value={filtreGroupe} onChange={e => setFiltreGroupe(e.target.value)}>
          <option value="">Groupe</option>
          {/* Options dynamiques à ajouter */}
        </select>
      </div>
      <div className="students-table-container">
        <table className="students-table">
          <thead>
            <tr>
              <th>N°</th>
              <th>Apogée</th>
              <th>Nom</th>
              <th>Prénom</th>
              <th>Statut</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {filtered.map((s, i) => (
              <tr key={s.id}>
                <td>{i + 1}</td>
                <td>{s.code_apogee}</td>
                <td>{s.nom}</td>
                <td>{s.prenom}</td>
                <td><span className={s.statut === 'actif' ? 'statut-actif' : 'statut-inactif'}>{s.statut === 'actif' ? 'Actif' : 'Inactif'}</span></td>
                <td>
                  <button className="btn-action" onClick={() => handleShowDetails(s)}>Détails</button>
                  <button className="btn-action" onClick={() => handleEdit(s)}>Éditer</button>
                  <button className="btn-action btn-supprimer" onClick={() => handleDelete(s)}>Supprimer</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
      {/* Pagination à ajouter si besoin */}
      {/* Modale détails/édition */}
      <StudentModal
        open={modalOpen}
        onClose={() => setModalOpen(false)}
        student={selectedStudent}
        mode={modalMode}
        onSave={handleSave}
      />
      {/* Modale de confirmation suppression */}
      {deleteModalOpen && (
        <div className="student-modal-overlay">
          <div className="student-modal">
            <p>Voulez-vous vraiment supprimer cet étudiant ?</p>
            <div style={{display:'flex',gap:'1em',justifyContent:'flex-end'}}>
              <button onClick={() => setDeleteModalOpen(false)}>Annuler</button>
              <button className="btn-supprimer" onClick={confirmDelete}>Supprimer</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default StudentsPage;
